import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const Post = () => {
  useEffect(() => {
    axios.get("http://localhost:3001/posts").then((response) => {
      setListOfPosts(response.data);
    });
  });

  let { id } = useParams();
  return <div>{id}</div>;
};

export default Post;
